import React, { Component } from 'react'
import { Button } from '@material-ui/core';

class CalculationComponent extends Component {
    constructor(props) {
      super(props)
      this.state = {
         height:this.props.height,
         weight:this.props.weight,
         bmi:'',
         bmiStatus:'',
         flag:false
      }
    }
    static getDerivedStateFromProps(nextProps, prevState) {
      if (nextProps.height !== prevState.height || nextProps.weight !== prevState.weight) {
        return {
          height: nextProps.height,
          weight: nextProps.weight,
        };
      }
      return null;
    }
  
    generateBMIstatus=(bmi)=>{
        if(bmi<18.5){
            return "You are underweight"
        }
        if((bmi>=18.5) && (bmi<24.9)){
            return "You are normal"
        }
        if((bmi>=25) && (bmi<29.9)){
            return "You are overweight"
        }
        if(bmi>=30){
            return "You are obese"
        }
    }

    getBMI=(e)=>{
      e.preventDefault();
      //bmi=w(kg)/h^2(m)-> cm=>m /100 so bmi= w*100*100/h^2
      // bmiVal=((weight/height)/height)*10000
      let bmiVal = (this.state.weight / (this.state.height / 100) ** 2).toFixed(2);
      console.log(bmiVal)
      this.setState({
          flag:true,
          bmi:bmiVal
      })
      console.log(this.state.bmi)
      let message=this.generateBMIstatus(bmiVal)
      this.setState({
        bmiStatus:message
      })
  }

  render() {
    return (
      <div>
        <Button variant="outlined" color="secondary" type="submit" onClick={this.getBMI}>SUBMIT</Button>
        {this.state.flag?
                <div>
                    <p>Your BMI: {this.state.bmi}</p>
                    <p>{this.state.bmiStatus}</p>
                </div>:<p></p>}
        
      </div>
    )
  }
}

export default CalculationComponent
