import React, { Component } from 'react'

class CalculationComponent extends Component {
    constructor(props) {
      super(props)
    
      this.state = {
         height:this.props.height,
         weight:this.props.weight,
         bmiStatus:''
      }
    }
    calculateBMI=()=>{
        let squareHeight=this.state.height*this.state.height
        let bmi=this.state.weight/squareHeight
        this.setState({
            bmi:bmi
        })
        return bmi
    }
    generateBmiStatus=(bmi)=>{
        if(bmi<18.5){
            return "You are Underweight";
        }
        if(bmi>=18.5 && bmi<24.9){
            return "You are Normal";
        }
        if(bmi>=25 && bmi<29.9){
            return "You are Overweight";
        }
        if(bmi>=30){
            return "You are Obese";
        }
    }
    
  render() {
    let bmiValue=calculateBMI()
    let message=generateBMIstatus()
    return (
      <div>
        <p>Your BMI is {bmiValue} and {message}</p>
      </div>
    )
  }
}

export default CalculationComponent
