import React, { Component } from 'react'
// import './components/app/App.css'
import CalculationComponent from '../calculator/CalculationComponent'

class FormComponent extends Component {
    constructor(props){
        super(props)
        this.state={
        height:'',
        weight:'',
        flag:false,
        // bmi:'',
        // bmiStatus:''
        }
    }
    changeHeightHandler=(event)=>{
        this.setState({
            height:event.target.value
        })
    }

    changeWeightHandler=(event)=>{
        this.setState({
            weight:event.target.value
        })
        this.setState({
            flag:true
        })
    }

    // getBMI=(height,weight)=>{
    //     //bmi=w(kg)/h^2(m)-> cm=>m /100 so bmi= w*100*100/h^2
    //     // bmiVal=((weight/height)/height)*10000
    //     this.setState({
    //         bmi:((weight/height)/height)*10000
    //     })
    // }

    // generateBMIstatus=(bmi)=>{
    //     if(bmi<18.5){
    //         return "!!!Under weight!!!"
    //     }
    //     if((bmi>=18.5) && (bmi<24.9)){
    //         return "!!!Normalweight!!!"
    //     }
    //     if((bmi>=25) && (bmi<29.9)){
    //         return "!!!!Overweight!!!!"
    //     }
    //     if(bmi>=30){
    //         return "!!!!!!!!Obesity!!!!!!!"
    //     }
    // }
    
    // submitHandler=(event)=>{
    //     this.setState({
    //         flag:true
    //     })
    //     event.preventDefault() // to prevent form render after submit
        // this.getBMI(this.state.height,this.state.weight)
        // this.setState({
        //     bmiStatus:this.generateBMIstatus(this.state.bmi)
        // }) 
    // }

  render() {
    return (
        <div>
            <h1>BMI CALCULATOR</h1>
            <form onSubmit={this.submitHandler}>
                <p>Enter your height in centimeters</p>
                <input type="number" step="any" value={this.state.height} onChange={this.changeHeightHandler}/>
                <p>Enter your weight in kilograms</p>
                <input type="number" step="any" value={this.state.weight} onChange={this.changeWeightHandler}/>
                {/* <button type="submit">SUBMIT</button> */}
                {this.state.flag?<CalculationComponent height={this.state.height} weight={this.state.weight}/>:<p></p>}
                {/* {this.state.flag?
                <div>
                    <p>Your BMI: {this.state.bmi}</p>
                    <p id="status">{this.state.bmiStatus}</p>
                </div>:<p></p>}
             */}
            </form>
        </div>
    )
  }
}


export default FormComponent
