import React, { Component } from 'react'
import './App.css'
import CalculationComponent from './CalculationComponent'

class FormComponent extends Component {
    constructor(props){
        super(props)
        this.state={
        height:'',
        weight:'',
        flag:false,
        bmi:'',
        bmiStatus:''
        }
    }
    changeHeightHandler=(event)=>{
        this.setState({
            height:event.target.value
        })
    }

    changeWeightHandler=(event)=>{
        this.setState({
            weight:event.target.value
        })
    }

    getBMI=(height,weight)=>{
        let squareHeight=height*height
        let bmiVal=weight/squareHeight
        this.setState({
            bmi:bmiVal
        })
        return bmiVal
    }

    generateBmiStatus=(bmi)=>{
        if(bmi<18.5){
            this.setState({
                bmiStatus:"You are Underweight"
            })
            return "You are Underweight";
        }
        if(bmi>=18.5 && bmi<24.9){
            this.setState({
                bmiStatus:"You are Normal"
            })
            return "You are Normal";
        }
        if(bmi>=25 && bmi<29.9){
            this.setState({
                bmiStatus:"You are Overweight"
            })
            return "You are Overweight";
        }
        if(bmi>=30){
            this.setState({
                bmiStatus:"You are Obese"
            })
            return "You are Obese";
        }
    }
    
    submitHandler=(event)=>{
        let calculatedBMI=getBMI(this.state.height,this.state.weight)
        let message=generateBMIstatus(this.state.bmi)
        this.setState({
            flag:true
        })
        event.preventDefault() // to prevent form render after submit
    }
    
  render() {
    return (
        <div>
            <h1>BMI CALCULATOR</h1>
            <form onSubmit={this.submitHandler}>
                <p>Enter your height in centimeters</p>
                <input type="number" step="any" value={this.state.height} onChange={this.changeHeightHandler}/>
                <p>Enter your weight in kilograms</p>
                <input type="number" step="any" value={this.state.weight} onChange={this.changeWeightHandler}/>
                <button type="submit">SUBMIT</button>
                {/* {this.state.flag?<CalculationComponent height={this.state.height} weight={this.state.weight}/>:<p></p>} */}
            </form>
        </div>
    )
  }
}

export default FormComponent
