import React, { Component } from 'react'
import CalculationComponent from '../calculator/CalculationComponent'

class FormComponent extends Component {
    constructor(props){
        super(props)
        this.state={
        height:'',
        weight:'',
        flag:false
        }
    }
    changeHeightHandler=(event)=>{
        this.setState({
            height:event.target.value
        })
    }

    changeWeightHandler=(event)=>{
        this.setState({
            weight:event.target.value
        })
        this.setState({
            flag:true
        })
    }

    render() {
    return (
        <div>
            <h1>BMI CALCULATOR</h1>
            <form onSubmit={this.submitHandler}>
                <input type="number" step="any" placeholder="Height in cms" value={this.state.height} onChange={this.changeHeightHandler}/>
                <input type="number" step="any" placeholder="Weight in kgs" value={this.state.weight} onChange={this.changeWeightHandler}/>
                {this.state.flag?
                    <CalculationComponent 
                        height={this.state.height} 
                        weight={this.state.weight}/>:
                    <p></p>
                }
            </form>
        </div>
    )
  }
}


export default FormComponent
