import react, {Component} from 'react';
import './App.css';

class App extends Component {
  constructor(){
    super()
    this.state={
      message:""
    }
    this.clickHandler=this.clickHandler.bind(this)
  }

  clickHandler(){
    this.setState({
      message:"Your BMI is "
    })
  }

  handleChange(event){
    let input_val=event.target.value;
    this.setState({
      value:input_val
    });
    this.props.onChange(input_val);
  }

  render(){
    return (
      <div>
            <h1>BMI CALCULATOR</h1>
            <div id="form">
                <p>Enter your height in centimeters</p>
                <input type="number" step="any" id="height" value={this.state.value} onChange={this.handleChange}/>
                <p>Enter your weight in kilograms</p>
                <input type="number" step="any" id="weight" value={this.state.value} onChange={this.handleChange}/>
                <div>
                  <button onClick={this.clickHandler}>SUBMIT</button>
                </div>
            </div>
        </div>
    );
  };
}

export default App;
