import react, {Component} from 'react';
import './App.css';

class App extends Component {
  constructor(){
    super()
    this.state={
      value:"",
      message:""
    }
    this.handleChange=this.handleChange.bind(this)
  }

  clickHandler(){
    this.setState({
      message:"Your BMI is "
    })
  }

  heightChange(event){
    let height=event.target.value;
    this.setState({
      value:height
    });
    this.props.onChange(height);
  }
  weightChange(event){
    let weight=event.target.value;
    this.setState({
      value:weight
    });
    this.props.onChange(weight);
  }

  render(){
    return (
      <div>
            <h1>BMI CALCULATOR</h1>
            <div id="form">
                <p>Enter your height in centimeters</p>
                <input type="number" step="any" id="height" value={this.state.value} onChange={this.heightChange}/>
                <p>Enter your weight in kilograms</p>
                <input type="number" step="any" id="weight" value={this.state.value} onChange={this.weightChange}/>
                <div>
                  <button onClick={this.clickHandler}>SUBMIT</button>
                </div>
            </div>
        </div>
    );
  };
}

export default App;
