import React, { Component } from 'react'
import { CssBaseline,Button,Grid, FormControl,InputAdornment,Typography,TextField} from '@mui/material';

class CmToFtForm extends Component {
  constructor(props) {
    super(props)
  
    this.state = {
       input:'',
       output:''
    }
    changeHandler=(event)=>{
      event.preventDefault();
      this.setState({
        input:event.target.value
      })
    }
    getOutput=(event)=>{
      event.preventDefault();
      this.setState({
        output:(this.state.input)
      })
    }
    
  }
  render(){
  return (
    <div>
      <CssBaseline />
            <Grid container xs display="flex" minHeight={450}
                            justifyContent="center" alignItems="center" 
                            style={{position:"absolute"}}>
            <FormControl>
            <Typography variant="h4" color="primary" gutterBottom>CM TO FT CONVERTER</Typography><br/>
                <TextField label="input" required
                        inputProps={{min:0}} type={"number"} step={"any" }
                        variant='outlined' size="medium" color="primary" 
                        InputProps={{endAdornment:<InputAdornment position="end">Cm</InputAdornment>}}
                        value={this.state.input} onChange={this.changeHandler}/>
                <Button variant="contained" color="primary" size="large" type="submit" 
                onClick={this.getOutput}>SUBMIT</Button>
            </FormControl>

            <Typography variant="subtitle1" color="primary" gutterBottom>
                      {this.state.input}Cms is {this.state.output}Ft 
                      </Typography>
            </Grid>
    </div>
  )
}
}

export default CmToFtForm
