import React, { Component } from 'react'

class CalculationComponent extends Component {
    constructor(props) {
      super(props)
    
      this.state = {
         height:this.props.height,
         weight:this.props.weight,
         bmi:'',
         bmiStatus:''
      }
    }

    getBMI=(height,weight)=>{
      //bmi=w(kg)/h^2(m)-> cm=>m /100 so bmi= w*100*100/h^2
      // bmiVal=((weight/height)/height)*10000
      this.setState({
          bmi:((weight/height)/height)*10000
      })
  }
  render() {
    this.getBMI(this.state.height,this.state.weight)
    // let message=this.generateBMIstatus()
    return (
      <div>
        <p>Your BMI is {this.state.bmi}</p>
      </div>
    )
  }
}

export default CalculationComponent
