import react, {Component} from 'react';
import './App.css';

class App extends Component {
  constructor(){
    super()
    this.state={
      h_value:'',
      w_value:'',
      message:''
    }
    this.heightChange=this.heightChange.bind(this)
    this.weightChange=this.weightChange.bind(this)
    this.clickHandler=this.clickHandler.bind(this)
  }

  changedHeight(height){
    this.setState({
      h_value:height
    })
  }
  changedWeight(weight){
    this.setState({
      w_value:weight
    })
  }

  heightChange(event){
    let height=event.target.value;
    this.setState({
      h_value:height
    });
    this.props.onChange(height);
  }
  weightChange(event){
    let weight=event.target.value;
    this.setState({
      w_value:weight
    });
    this.props.onChange(weight);
  }


  render(){
    return (
      <div>
            <h1>BMI CALCULATOR</h1>
            <div id="form">
                <p>Enter your height in centimeters</p>
                <input type="number" step="any" id="height" value={this.state.h_value} onChange={this.heightChange}/>
                <p>Enter your weight in kilograms</p>
                <input type="number" step="any" id="weight" value={this.state.w_value} onChange={this.weightChange}/>
                <div>
                  <button onClick={this.clickHandler}>SUBMIT</button>
                </div>
            </div>
        </div>
    );
  };
}

export default App;
