    //         return "You are Overweight";
    //     }
    //     if(bmi>=30){
    //         return "You are Obese";
    //     }
    // }
    
  render() {
    let bmiValue=this.calculateBMI()
    // let message=this.generateBMIstatus()
    return (
      <div>
        <p>Your BMI is {bmiValue}</p>
      </div>
    )
  }
}

export default CalculationComponent
