import React, { Component } from 'react'
import './App.css'

class CalculationComponent extends Component {
    constructor(props) {
      super(props)
    
      this.state = {
         height:this.props.height,
         weight:this.props.weight,
         bmi:'',
         bmiStatus:'',
         flag:false
      }
    }

    generateBMIstatus=(bmi)=>{
        if(bmi<18.5){
            return "!!!Under weight!!!"
        }
        if((bmi>=18.5) && (bmi<24.9)){
            return "!!!Normalweight!!!"
        }
        if((bmi>=25) && (bmi<29.9)){
            return "!!!!Overweight!!!!"
        }
        if(bmi>=30){
            return "!!!!!!!!Obesity!!!!!!!"
        }
    }

    getBMI=(e)=>{
      e.preventDefault();
      //bmi=w(kg)/h^2(m)-> cm=>m /100 so bmi= w*100*100/h^2
      // bmiVal=((weight/height)/height)*10000
      let bmiVal=(this.state.weight/this.state.height)/this.state.height
      this.setState({
          flag:true,
          bmi:(bmiVal)*10000
      })
      let message=this.generateBMIstatus(this.bmi)
      this.setState({
        bmiStatus:message
      })
  }

  // submitHandler=(event)=>{
  //   event.preventDefault()
  //   this.setState({
  //       flag:true
  //   })   
  //   this.getBMI(this.state.height,this.state.weight)
  //   this.setState({
  //     bmiStatus:this.generateBMIstatus(this.state.bmi)
  //   })  
  // }

  render() {
    return (
      <div>
        <button type="submit" onClick={this.getBMI}>SUBMIT</button>
        {/* {this.state.flag?this.getBMI(this.state.height,this.state.weight):<p></p>}
        <p>Your BMI is {this.state.height}
        {this.state.weight} {this.state.bmi}</p> */}
        {this.state.flag?
                <div>
                    <p>Your BMI: {this.state.bmi}</p>
                    <p id="status">{this.state.bmiStatus}</p>
                </div>:<p></p>}
        
      </div>
    )
  }
}

export default CalculationComponent
