import './App.css';
import React, { Component } from 'react'

export class App extends Component {

  constructor(props){
    super(props)
    this.state={
      height:'',
      weight:'',
      bmiStatus:'',
      bmiValue:''
    }
  }

  changeHeightHandler=(e)=>{
    this.setState({
      height:e.target.value
    })
  }

  changeWeightHandler=(e)=>{
    this.setState({
      weight:e.target.value
    })
  }

  calculateBMI(height,weight){
    let squareHeight=height*height
    let calculatedValue=weight/squareHeight
    return calculatedValue
  }

  clickHandler(){
    let bmi=calculateBMI(height,weight)
    let message=generateBmiClass(bmi)
    this.setState({
        bmiValue:bmi,
        bmiStatus:message
    })
  }

  render() {
    return (
      <div>
          <h1>BMI CALCULATOR</h1>
          <div id="form">
              <p>Enter your height in centimeters</p>
              <input type="number" step="any" id="height" value={this.state.height} onChange={this.changeHeightHandler}/>
              <p>Enter your weight in kilograms</p>
              <input type="number" step="any" id="weight" value={this.state.weight} onChange={this.changeWeightHandler}/>
              <div>
                  <button onClick={this.clickHandler}>SUBMIT</button>
                  <h3>BMI = {this.state.bmi}</h3>
                  <h3>{this.state.bmiClass}</h3>
              </div>
          </div>
        </div>
    );
  }
};

export default App;
