  //   let weight=event.target.value;
  //   this.setState({
  //     w_value:weight
  //   });
  //   this.props.onChange(weight);
  // }


  render(){
    return (
      <div>
            <h1>BMI CALCULATOR</h1>
            <div id="form">
                <p>Enter your height in centimeters</p>
                <input type="number" step="any" id="height" value={value} onChange={(e)=>setState(e.target.value)}/>
                <p>Enter your weight in kilograms</p>
                <input type="number" step="any" id="weight" value={value} onChange={(e)=>setState(e.target.value)}/>
                <div>
                  <button onClick={this.clickHandler}>SUBMIT</button>
                  <h3>BMI = {this.state.bmi}</h3>
                  <h3>{this.state.bmiClass}</h3>
                </div>
            </div>
        </div>
    );
  };
}

export default App;
