import React, { Component } from 'react'
import CalculationComponent from '../calculator/CalculationComponent'
import { CssBaseline,Container, FormControl,InputAdornment,Box,Typography,TextField } from '@mui/material';

class FormComponent extends Component {
    constructor(props){
        super(props)
        this.state={
        height:'',
        weight:'',
        flag:false
        }
    }
    changeHeightHandler=(event)=>{
        this.setState({
            height:event.target.value
        })
    }

    changeWeightHandler=(event)=>{
        this.setState({
            weight:event.target.value
        })
        this.setState({
            flag:true
        })
    }

    render() {
    return (
        <div>
            <React.Fragment>
                <CssBaseline />
                <Container fixed>
                    <Box sx={{ bgcolor: 'primary.light', height: '100vh' }} />
                </Container>
            <FormControl>
                <Typography variant="h4" color="primary" gutterBottom>BMI CALCULATOR </Typography>
                <TextField 
                        type="number" step="any" placeholder="Height"
                        variant='outlined' size="medium" color="primary"
                        InputProps={{endAdornment:<InputAdornment position="end">Cm</InputAdornment>}}
                        value={this.state.height} onChange={this.changeHeightHandler}/>
                <br/>
                <TextField 
                        type="number" step="any" placeholder="Weight" 
                        variant='outlined' size="medium" color="primary"
                        InputProps={{endAdornment:<InputAdornment position="end">Kg</InputAdornment>}} 
                        value={this.state.weight} onChange={this.changeWeightHandler}/>
                {this.state.flag?
                    <CalculationComponent 
                        height={this.state.height} 
                        weight={this.state.weight}/>:
                    <p></p>
                }
            </FormControl>
            </React.Fragment>
        </div>
    )
  }
}


export default FormComponent
