import react, {Component} from 'react';
import './App.css';

class App extends Component {
  constructor(){
    super()
    this.state={
      h_value:'',
      w_value:'',
      message:''
    }
    this.heightChange=this.heightChange.bind(this)
    this.weightChange=this.weightChange.bind(this)
    this.clickHandler=this.clickHandler.bind(this)
  }

  changedHeight(height){
    this.setState({
      h_value:height
    })
  }
  changedWeight(weight){
    this.setState({
      w_value:weight
    })
  }

  clickHandler(){
    let square_height=this.state.height*this.state.height;
    let b_value=this.state.w_value/square_height;
    this.setState({
      bmi:b_value
    });

    let bmiClass = this.getBmi(b_value);
    this.setState({
      bmiClass:bmiClass
    });
  }

  getBmi(bmi) {
    if(bmi < 18.5) {
        return "Underweight";
    }
    if(bmi >= 18.5 && bmi < 24.9) {
        return "Normal weight";
    }
    if(bmi >= 25 && bmi < 29.9) {
        return "Overweight";
    }
    if(bmi >= 30) {
        return "Obesity";
    }
}

  heightChange(event){
    let height=event.target.value;
    this.setState({
      h_value:height
    });
    this.props.onChange(height);
  }
  weightChange(event){
    let weight=event.target.value;
    this.setState({
      w_value:weight
    });
    this.props.onChange(weight);
  }


  render(){
    return (
      <div>
            <h1>BMI CALCULATOR</h1>
            <div id="form">
                <p>Enter your height in centimeters</p>
                <input type="number" step="any" id="height" value={this.state.h_value} onChange={this.heightChange}/>
                <p>Enter your weight in kilograms</p>
                <input type="number" step="any" id="weight" value={this.state.w_value} onChange={this.weightChange}/>
                <div>
                  <button onClick={this.clickHandler}>SUBMIT</button>
                  <h3>BMI = {this.state.bmi}</h3>
                  <h3>{this.state.bmiClass}</h3>
                </div>
            </div>
        </div>
    );
  };
}

export default App;
